var tokenize = require('../tokenize');
var expect = require('chai').expect;

describe('tokenizer', function() {
  it('splits text into words', function() {
    expect(tokenize("John's mom went there, but he wasn't there. So she said---: 'Where are you!!'")).to
      .have.members(["John's", "mom", "went", "there", "but", "he", "wasn't", "there", "So", "she", "said", "Where", "are", "you"])
  });
});